import React, { useState } from 'react';
import './SellerLogin.css'; // Import custom CSS for styling

const SellerLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Implement your seller login logic here.
    // For simplicity, we'll just display the entered username and password.
    alert(`Username: ${username}\nPassword: ${password}`);
  };

  return (
    <div className="seller-login-container">
      <div className="seller-login-box">
        <h2 className="text-center">Seller Login</h2>
        <form>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              className="form-control"
              id="username"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              className="form-control"
              id="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="form-group mt-3"> {/* Added margin-top to create space */}
            <button
              type="button"
              className="btn btn-primary btn-block"
              onClick={handleLogin}
            >
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SellerLogin;
