import React, { useState } from 'react';

const Seller = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    // Implement your seller login logic here.
    // Check the username and password, and set loggedIn to true if authentication is successful.
    if (username === 'seller' && password === 'password') {
      setLoggedIn(true);
    }
  };

  return (
    <div className="container">
      <h2>Seller Login</h2>
      {loggedIn ? (
        <div>
          <p>Welcome, {username}!</p>
          {/* Display seller dashboard or content here */}
        </div>
      ) : (
        <div>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
};

export default Seller;
